Board map;

void SetColor(int value) {
	SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), value);
}
void display(char displayBoard[38][53])
{
	for (int x = 0; x < 38; x++)
	{
		for (int y = 0; y < 53; y++)
		{
			if (displayBoard[x][y] == 'P')
			{
				SetColor(14);
				printf("%c",displayBoard[x][y]);
			}
			else if (displayBoard[x][y] == 'Z')
			{
				SetColor(3);
				printf("%c", displayBoard[x][y]);
			}
			else if (displayBoard[x][y] == '*')
			{
				SetColor(7);
				printf("%c", displayBoard[x][y]);
			}
			else {
				SetColor(2);
				printf("%c", displayBoard[x][y]);
			}
		}
		printf("\n");
	}
	SetColor(15);
}

void setBoard()
{
	for (int x = 0; x < 8; x++)
	{
		for (int y = 'A'; y <= 'H'; y++)
		{
			map.addVertex(x, y);
		}
	}

	for (int x = 1; x < 7; x++)
	{
		for (char y = 'B'; y < 'H'; y++)
		{
			map.addEdge(x, y, x + 1, y);
			map.addEdge(x, y, x, y +1);
		}
	}
	for (int x = 0; x < 7; x++)
	{
		map.addEdge(x, 'A', x + 1, 'A');
		map.addEdge(x, 'A', x, 'B');
	}
	for (char y = 'A'; y < 'H'; y++)
	{
		map.addEdge(0, y, 1, y);
		map.addEdge(0, y, 0, y+1);
		map.addEdge(7, y, 7, y + 1);
	}

	for (int x = 0; x < 7; x++)
	{
		map.addEdge(x, 'H', x + 1, 'H');
	}
	//map.addVertex(0, 'A', );
}
